    private void comparateButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comparateButtonActionPerformed
        
        double A = Integer.parseInt(fieldA.getText());
        double B = Integer.parseInt(fieldB.getText());
        
        Comparador comparador = new Comparador(A, B);

        String comparate = comparador.comparar();
        
        fieldResponse.setText(comparate);
    }//GEN-LAST:event_comparateButtonActionPerformed

